import { createContext, useState, useContext } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {


    const addToCart = (product) => {
    setCart((prev) => {
      // Agar product already cart me hai
      const exist = prev.find((item) => item.id === product.id);
      if (exist) {
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };
  const [cart, setCart] = useState([
    { id: 1, name: "Shalwar Kameez", description:"perfect for both casual and formal wear Comfortable, stylish, and suitable for all occasions." ,  price: 2500, quantity: 1, image: "data:image/jpeg;base64," },
    { id: 2, name: "Kurti", description:"Light, elegant, and ideal for daily wear or festive gatherings. Comes in a variety of fabrics and patterns.", price: 1500, quantity: 1, image: "data:image/jpeg;base64," },
    { id: 3, name: "Sherwani", description:"Traditionally worn by grooms and for ceremonial events. Adds sophistication and grandeur to the look",price: 8000, quantity: 1, image: "data:image/jpeg;base64," },
    { id: 4, name: "Lehenga",description:" Popular for weddings, parties, and festive occasions..", price: 12000, quantity: 1, image: "data:image/jpeg;base64," },
    { id: 5, name: "Dupatta",description:"  It adds grace and style while complementing traditional outfits.", price: 500, quantity: 1, image: "data:image/jpeg;base64," },
    { id: 6, name: "Waistcoat",description:"Perfect for weddings, parties, or semi-formal events, offering a classy and refined look.", price: 3000, quantity: 1, image: "data:image/jpeg;base64," },
  ]);
  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };
  return (
   

<CartContext.Provider value={{ cart, setCart, addToCart, removeFromCart }}>
  {children}
</CartContext.Provider>
  );
};
export const useCart = () => useContext(CartContext);